import React from "react";
import { Img, List, Text } from "components";
import Header from "components/Header";
import ItemCard from "components/ItemCard";
import { itemsData, getItemImage } from "components/ItemCard/itemData";
const AssortmenPage: React.FC = () => {
  const itemSpacing = "225px"; // Set the desired spacing

  return (
    <>
      <div className="bg-indigo-A100 flex flex-col font-balootamma items-center justify-start mx-auto pb-2.5 pr-2.5 w-full">
        <div className="flex flex-col items-center justify-start max-w-[1910px] mb-[395px] mx-auto md:px-5 w-full">
          <Header className="flex md:flex-col flex-row md:gap-5 items-center justify-center w-full" />
          <div className="flex md:flex-col flex-row md:gap-10 gap-[75px] items-center justify-start mt-[31px] w-[74%] md:w-full">
            <div className="flex items-center justify-start w-full">
              {itemsData.map((item) => (
                <ItemCard key={item.id} {...item} style={{ itemSpacing }}>
                  {getItemImage(item.imageSrc)}
                </ItemCard>
              ))}
            </div>
          </div>
          <div className="flex flex-row items-center justify-between mt-3 w-[86%] md:w-full"></div>
        </div>
      </div>
    </>
  );
};

export default AssortmenPage;
