// itemsData.js
import React from "react";
import { Img } from "components/Img";

export const itemsData = [
  {
    id: 1,
    title: "Pants",
    description: "Blue colored pants as on the model",
    price: "$29.99",
    imageSrc: "img_blue12.png",
  },
  {
    id: 2,
    title: "Shirt",
    description: "Powder blue shirt",
    price: "$19.99",
    imageSrc: "img_blue21.png",
  },
  {
    id: 3,
    title: "Jacket",
    description: "Horse leather jacket",
    price: "$49.99",
    imageSrc: "img_blue31.png",
  },
];

export const getItemImage = (imageName: string): React.ReactElement => {
  return <Img src={imageName} alt="Item Image" />;
};
