// src/components/ItemCard/index.tsx
import React from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

interface ItemCardProps {
  imageSrc: string;
  title: string;
  description: string;
  buttonLabel: string;
  customStyle?: React.CSSProperties;
}

const ItemCard: React.FC<ItemCardProps> = ({
  imageSrc,
  title,
  description,
  buttonLabel,
  customStyle,
}) => {
  return (
    <Card style={{ width: "18rem", ...customStyle }}>
      <Card.Img variant="top" src={imageSrc} />
      <Card.Body>
        <Card.Title className="txtBalooRegular24">{title}</Card.Title>
        <Card.Text className="txtBalooRegular24">{description}</Card.Text>
        <Button variant="primary" className="txtBalooRegular24">
          {buttonLabel}
        </Button>
      </Card.Body>
    </Card>
  );
};

export default ItemCard;
